var btn=document.querySelector(".slider-button"),form=document.querySelector(".modal-form"),overlay=document.querySelector(".overlay"),close=document.querySelector(".btn-close"),input=form.querySelector("input");btn.addEventListener("click",function(){form.classList.add("show"),overlay.classList.add("show"),input.focus()}),close.addEventListener("click",function(){form.classList.remove("show"),overlay.classList.remove("show")}),window.addEventListener("keydown",function(e){"Escape"===e.key&&form.classList.contains("show")&&(form.classList.remove("show"),overlay.classList.remove("show"))});